#!/bin/bash
#
# Force Upload to Qdrant via REST API (Battering Ram Approach)
# -------------------------------------------------------------
# Uploads prepared payloads from ready_to_curl.json to Qdrant
# using curl to bypass SSL/connection issues with Python client.
#

set -e  # Exit on error

# Configuration
SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
PAYLOAD_FILE="${SCRIPT_DIR}/ready_to_curl.json"
QDRANT_URL="${QDRANT_URL:-https://nuzantara-qdrant.fly.dev}"
QDRANT_API_KEY="${QDRANT_API_KEY:-QDD0rKHU2UMHqohUmn4iAI3umrZdQxoVI9sAufKaZyXWjZyeaBzCEpO5GlERjJHo}"

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Check if payload file exists
if [ ! -f "$PAYLOAD_FILE" ]; then
    echo -e "${RED}❌ Error: Payload file not found: $PAYLOAD_FILE${NC}"
    echo "   Run prepare_payloads.py first to generate ready_to_curl.json"
    exit 1
fi

# Extract collection name and points count using Python
COLLECTION_NAME=$(python3 -c "import json; data = json.load(open('$PAYLOAD_FILE')); print(data['collection'])")
TOTAL_POINTS=$(python3 -c "import json; data = json.load(open('$PAYLOAD_FILE')); print(len(data['points']))")

